import React, { Component } from 'react';
import '../signInUp.css';

class SignUp extends Component {
    state = {
        buttonClick: 0
    };
    render() {
        return ( 
            <div className="signInUp">
                <h1>Create Account</h1>
                <form>
                    <input type="text" name="firstName" placeholder="First Nmae"/>
                    <input type="text" name="lastName" placeholder="last Nmae"/>
                    <input type="email" name="email" placeholder="Email"/>
                    <input type="text" name="username" placeholder="Username"/>
                    <input type="password" name="password" placeholder="Password"/>
                    <p><a href="javascript: void(0)" onClick={this.extandNavBar} id = "a"  >Log in</a></p>
                    <button>Sign up</button>
                </form>
            </div>
        );
    }
    increment = () => {
        this.state.buttonClick++;
        const counter = this.state.buttonClick;
        this.setState({counter});

        // return counter;
    }
    extandNavBar = () => {
        let col;
        console.log( this.state.buttonClick );
        col = (this.state.buttonClick % 2 == 0) ? document.getElementById( "a" ).style.display = "none": document.getElementById( "a" ).style.display = "block";
        return col;
    }
}


export default SignUp;
